#include <iostream> // cout, endl, cin
#include <set> // set

using namespace std;

#define rep(i,n) for (int i = 0; i < (n); ++i)

typedef long long ll;

int main(){

  int s;
  cin >> s;

  set<int> st;
  st.insert(s);

  int current = s;
  while (true) {
    if(current % 2 == 0) {
      current /= 2;
    } else {
      current = 3 * current + 1;
    }

    if(st.count(current) > 0) { // Use set's count function to check for presence
      cout << st.size() + 1 << endl; // Print the size of the set plus one for the repetition point
      return 0;
    }
    st.insert(current);
  }

  return 0;
}
