#include <bits/stdc++.h>

#define ll long long
#define endl "\n"

using namespace std;

inline int D()
{
    int t;
    scanf("%d", &t);
    return t;
}

inline ll llD()
{
    ll t;
    scanf("%lld", &t);
    return t;
}

const int Max = 1e6 + 9;
ll a[Max];
ll dp[Max];

int main()
{
    n = D();
    m = D();
    for (int i = 0; i < n; i++)
    {
        a[i] = llD();
    }

    for (int i = 0; i < n; i++)
    {
        dp[i] = LLONG_MAX;
    }

    dp[n-1] = 0; // Base case

    for (int i = n - 2; i >= 0; i--)
    {
        for (int j = 1; j <= m && i + j < n; j++)
        {
            dp[i] = min(dp[i], dp[i + j] + abs(a[i] - a[i + j]));
        }
    }

    printf("%lld", dp[0]);
}
