#include <bits/stdc++.h>

#define ll long long
#define endl "\n"

using namespace std;

const int Max = 1e6 + 9;
ll a[Max];

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    int n, m;
    cin >> n >> m;
    for (int i = 0; i < n; i++)
    {
        cin >> a[i];
    }

    vector<ll> dp(n, LLONG_MAX);

    dp[n-1] = 0; // Base case

    for (int i = n - 2; i >= 0; i--)
    {
        for (int j = 1; j <= m && i + j < n; j++)
        {
            dp[i] = min(dp[i], dp[i + j] + abs(a[i] - a[i + j]));
        }
    }

    cout << dp[0];
    return 0;
}
