#include <bits/stdc++.h>

using namespace std;

#define IO ios::sync_with_stdio(false), cin.tie(0)

typedef long long ll;

const int inf = 999999999;

int main()
{
    IO;

    int n, k;
    cin >> n >> k;

    // Allocate memory with default initialization
    vector<ll> arr(n + 1, 0); // Use 0 as default value for arr
    vector<ll> dp(n + 1, inf); // Use inf as default value for dp

    for (int i = 1; i <= n; ++i)
        cin >> arr[i];

    dp[1] = 0;

    for (int i = 2; i <= n; ++i)
    {
        for (int j = 1; j <= k; ++j)
        {
            dp[i] = min(dp[i], dp[max(i - j, 1)] + abs(arr[i] - arr[max(i - j, 1)]));
        }
    }

    cout << dp[n] << endl;
}
