#include<bits/stdc++.h>

using namespace std;

int main(){
  int s;
  cin >> s;
  vector<int> a(10000, 0);
  a.at(s % 10000)++;
  for(int i=2; ; i++){
    if(s%2 == 0){
      s /= 2;
    }else{
      s = s * 3 + 1;
    }
    int mod_s = s % 10000;
    a.at(mod_s)++;
    if(a.at(mod_s) == 2){
      cout << i << endl;
      break;
    }
  }
  return 0;
}
