#include <bits/stdc++.h>

using namespace std;

int main() {
  int s; cin >> s;
  unordered_set<int> se; // Use unordered set for faster lookup
  int current = s;
  int pre = current;
  int i = 1;

  // Single loop to generate sequence and check for repeats
  while (se.find(current) == se.end()) { // Check if current already exists in the set
    se.insert(current); // Insert the current into the set after checking
    i++;
    if (current % 2 == 0) {
      pre = current / 2;
    } else {
      pre = 3 * current + 1;
    }
    current = pre;
  }

  cout << i << endl;
  return 0;
}
