#include <iostream>
#include <unordered_set>

using namespace std;

typedef long long ll;

// Welcome to my optimized source code!

int main() {
    int s;
    cin >> s;

    unordered_set<int> seen;
    seen.insert(s);

    int ans;

    for (int i = 1; ; i++) {
        if (s % 2 == 0) s /= 2;
        else s = 3 * s + 1;

        if (seen.count(s)) {
            ans = i + 1;
            break;
        }
        seen.insert(s);
    }

    cout << ans << endl;
}
