#include <iostream>
#include <unordered_set>

using namespace std;

typedef long long ll;

// Welcome to my optimized source code!

int main() {
    int s;
    cin >> s;

    unordered_set<int> seen;
    seen.insert(s);

    int ans;

    int i = 0;
    while (true) {
        i++;
        if (s % 2 == 0) s /= 2;
        else s = 3 * s + 1;

        if (!seen.insert(s).second) {
            ans = i + 1;
            break;
        }
    }

    cout << ans << endl;
}
