#include <iostream>
#include <cmath>

#define INF 1e9
#define MAX 1000006

using namespace std;

int n, k;
int ara[MAX], dp[MAX];

int main() {
    ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0);

    cin >> n >> k;

    for (int i = 0; i < n; i++) cin >> ara[i];

    dp[n-1] = 0;

    for (int id = n - 2; id >= 0; id--) {
        dp[id] = INF;
        for (int i = 1; i <= k && id + i < n; i++) {
            dp[id] = min(dp[id], abs(ara[id + i] - ara[id]) + dp[id + i]);
        }
    }

    cout << dp[0] << endl;

    return 0;
}
