#include <iostream>
#include <algorithm>
#include <cstring>
using namespace std;

#define pr(x) cout<<x
#define prs cout<<" "
#define prn cout<<"\n"
#define sc(x) cin>>x
#define ll long long int

int n, k;
int a[1000005];
int dp[1000005];

int32_t f(int32_t i)
{ 
    if(i == (n-1)) return 0;
    if(i >= n) return INT_MAX;
    if(dp[i] != -1) return dp[i];
    int32_t ans = INT_MAX; // Use 'ans' for minimum
    for(int32_t j=1; j<=k; j++)
    {
        if(j+i > n-1) break;
        else ans = min(ans, abs(a[i] - a[i+j]) + f(i+j));
    }
    return dp[i] = ans; // Assign and return dp[i] directly
}

int main()
{
    ios :: sync_with_stdio(false); cin.tie(0);
    int t=1;
    for(int i9 = 1; i9 <= t; i9++)
    {
        cin >> n >> k;
        memset(dp, -1, sizeof(dp));
        for(int i=0; i<n; i++)
            cin >> a[i];
        int ans = f(0);
        cout << ans << endl;
    }
}
