#include <bits/stdc++.h>

#define INF 0x3f3f3f3f

using namespace std;

const int maxn = 1e5+5;

int h[maxn];

int d[maxn];

int main()
{
    ios::sync_with_stdio(0);
    cin.tie(0);

    int n, k;

    memset(d, INF, sizeof d);

    cin >> n >> k;

    for (int i = 1; i <= n; i++)
    {
        cin >> h[i];
    }

    d[1] = 0;

    for (int i = 2; i <= n; i++)
    {
        d[i] = d[i-1] + abs(h[i] - h[i-1]);
        for (int j = 2; j <= k && i - j >= 1; j++)
        {
            d[i] = min(d[i], d[i-j] + abs(h[i] - h[i-j]));
        }
    }

    cout << d[n] << '\n';

    return 0;
}
