#include "bits/stdc++.h"

#define ll long long

#define PI 3.1415926535897932384626433832795l

#define MAX_NUM 1000000000

using namespace std;

ll h[1000000];
ll dp[1000000];

int main() {
    ll n, k;
    cin >> n >> k;

    for (int i = 0; i < n; i++) {
        cin >> h[i];
    }

    dp[0] = 0;
    for (int i = 1; i < n; i++) {
        dp[i] = MAX_NUM;
        for (int j = 1; j <= k; j++) {
            if (i - j >= 0) {
                dp[i] = min(dp[i], fabs(h[i] - h[i - j]) + dp[i - j]);
            }
        }
    }

    printf("%lld\n", dp[n - 1]);
    return 0;
}
