#include <bits/stdc++.h>

#define endl "\n"

#define ll long long int

#define vi vector<int>

#define vll vector<ll>

#define vvi vector < vi >

#define pii pair<int,int>

#define pll pair<long long, long long>

#define mod 1000000007

#define inf 1000000000000000001LL;

#define all(c) c.begin(),c.end()

#define mp(x,y) make_pair(x,y)

#define mem(a,val) memset(a,val,sizeof(a))

#define pb push_back

#define f first

#define se second

using namespace std;

int main()
{
    std::ios::sync_with_stdio(false);

    ll n=0, k2=0;

    cin >> n >> k2;

    vector<ll> arr(n);
    vector<ll> dp(n, inf);

    for (ll i = 0; i < n; ++i) {
        cin >> arr[i];
    }

    dp[n-1] = 0; // Base case

    for (ll cur = n-2; cur >= 0; --cur) {
        for (ll i = 1; i <= k2 && cur + i < n; ++i) {
            dp[cur] = min(dp[cur], dp[cur + i] + abs(arr[cur] - arr[cur + i]));
        }
    }

    cout << dp[0];

    return 0;
}
