#include<iostream>
#include<cstdio>
#include<cstring>
#include<climits>

using namespace std;

int n, k, f[1100000], w[1100000];

inline int xg(int a) {
    return (a < 0) ? -a : a;
}

int main()
{
    cin >> n >> k;

    for (int i = 0; i < n; i++) {
        f[i] = INT_MAX;
    }

    for (int i = 0; i < n; i++)
    {
        cin >> w[i];
    }

    f[0] = 0;

    for (int i = 1; i < n; i++)
    {
        for (int j = 1; j <= k; j++)
        {
            if (i - j < 0) continue;
            f[i] = min(f[i], f[i - j] + xg(w[i - j] - w[i]));
        }
    }

    if (f[n - 1] == INT_MAX) {
        cout << 0;
    } else {
        cout << f[n - 1];
    }
}
