#include <bits/stdc++.h>

using namespace std;

int main() {
    set<int> set1;
    int s;
    cin >> s;
    set1.insert(s);

    for(int i = 1; i < 1000000; i++) {
        if(s % 2 == 0) {
            s /= 2;
        } else {
            s = 3 * s + 1;
        }
        if(set1.insert(s).second == false) {
            cout << i + 1 << endl;
            return 0;
        }
    }
