using namespace std;

int main() {
    unordered_set<int> a;
    int s;
    cin >> s;

    for (int i = 0; i < 1000000; i++) {
        if (a.find(s) != a.end()) {
            cout << i + 1 << endl;
            return 0;
        }
        a.insert(s);
        if (s % 2 == 0) {
            s /= 2;
        } else {
            s = 3 * s + 1;
        }
    }
