#include <bits/stdc++.h>

using namespace std;

int main() {
    unordered_set<int> a;
    int s;
    cin >> s;

    for (int i = 0; i < 1000000; i++) {
        // Apply the operation first
        if (s % 2 == 0) {
            s /= 2;
        } else {
            s = 3 * s + 1;
        }

        // Check if 's' is already in the set
        if (a.find(s) != a.end()) {
            cout << i + 1 << endl;
            return 0;
        }

        a.insert(s);
    }
}
