#include <bits/stdc++.h>

using namespace std;

int main() {
    unordered_set<int> a;
    int s;
    cin >> s;

    for (int i = 0; i < 1000000; i++) {
        int original_s = s; // Store the original value of s

        // Apply the operation first
        if (s % 2 == 0) {
            s /= 2;
        } else {
            s = 3 * s + 1;
        }

        // Add the original number to the set before checking for repetition
        a.insert(original_s);
        // Check for repetition by determining if the size of the set has not increased
        if (a.find(s) != a.end()) {
            cout << i + 1 << endl;
            return 0;
        }
    }
}
