#include <bits/stdc++.h>

using namespace std;

typedef long long int ll;

#define FAST ios_base::sync_with_stdio(false); cin.tie(nullptr);

void solve() {
    int numElements, maxJump; cin >> numElements >> maxJump;

    vector<int> heights(numElements + 1); // Changed to 1-indexed

    for (int i = 1; i <= numElements; ++i) { // Changed to 1-indexed
        cin >> heights[i];
    }

    vector<int> minCost(numElements + 2, INT_MAX); // Dynamic size of minCost array
    minCost[1] = 0; // Start from index 1

    for (int i = 1; i <= numElements; ++i) { // Changed to 1-indexed
        int maxReach = min(i + maxJump, numElements); // Calculate maxReach only once
        for (int j = i + 1; j <= maxReach; j++) { // Changed inner loop to run from i+1 to maxReach
            minCost[j] = min(minCost[j], minCost[i] + abs(heights[i] - heights[j]));
        }
    }

    cout << minCost[numElements] << "\n"; // Output the result from index numElements
}

int main() {
    FAST
    solve();
    return 0;
}
