#include <bits/stdc++.h>

using namespace std;

#define int ll
#define endl "\n"
#define MAX 500005

typedef long long ll;

int arr[MAX];

signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int n, k;
    cin >> n >> k;

    for (ll i = 1; i <= n; i++) cin >> arr[i];

    vector<int> dp(n + 1, LLONG_MAX);
    dp[1] = 0;

    for (ll i = 2; i <= n; i++) {
        for (int j = i - 1; j >= i - k && j > 0; j--) {
            dp[i] = min(dp[i], dp[j] + abs(arr[i] - arr[j]));
        }
    }

    cout << dp[n] << endl;
    return 0;
}
