#include <bits/stdc++.h>

using namespace std;

#define int ll
#define endl "\n"
#define vi vector<int>
#define For(i,s,e) for (ll i=(s); i<(e); i++)
#define MAX 500005

typedef long long ll;

const ll INF = 1e15; // Define a constant for infinity

int arr[MAX];

signed main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    int n, k;
    cin >> n >> k;
    For(i, 1, n+1) cin >> arr[i];

    vector<int> dp(n + 1, INF); // Initialize dp array with INF
    dp[1] = 0;

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= k; ++j) {
            if (i + j <= n) {
                dp[i + j] = min(dp[i + j], dp[i] + abs(arr[i + j] - arr[i]));
            }
        }
    }

    cout << dp[n] << endl;

    return 0;
}
