#include<bits/stdc++.h>

using namespace std;

int main(){
  int s;
  cin >> s;
  vector<int> numbers_seen;
  numbers_seen.push_back(s % 10000);
  for(int i=2; i <= 10000; i++){
    s = (s % 2 == 0) ? s / 2 : s * 3 + 1;
    int mod_s = s % 10000;
    if(find(numbers_seen.begin(), numbers_seen.end(), mod_s) != numbers_seen.end()){
      cout << i << endl;
      break;
    }
    numbers_seen.push_back(mod_s);
  }
  return 0;
}
