#include <bits/stdc++.h>
#define maxn 1000010
#define ll long long
#define inf 0x3f3f3f3f
using namespace std;

ll arr[maxn];
ll dp[maxn];
ll n, k;

int main()
{
    memset(dp, 0, sizeof dp);
    scanf("%lld%lld", &n, &k);

    for (int i = 1; i <= n; i++)
    {
        dp[i] = inf;
        scanf("%lld", &arr[i]);
    }

    dp[1] = 0;
    dp[2] = abs(arr[2] - arr[1]);
    for (int i = 2; i <= n; i++)
    {
        int limit = max(i - k, 1ll);
        for (int j = i - 1; j >= limit; j--)
            dp[i] = min(dp[i], dp[j] + abs(arr[i] - arr[j]));
    }

    cout << dp[n] << endl;
    return 0;
}
