#include <bits/stdc++.h>

using namespace std;

int main() {
  int s; cin>>s;
  set<int> se;
  int current = s;
  se.insert(current);

  int i = 0;
  while (true) {
    if (current % 2 == 0) {
      current /= 2;
    } else {
      current = 3 * current + 1;
    }
    i++;
    if (se.find(current) != se.end()) {
      cout << i + 1 << endl;
      return 0;
    }
    se.insert(current);
  }
}
