#include <bits/stdc++.h>

using namespace std;

int main() {
  int s; cin>>s;
  set<int> se = {s}; // Initialize the set with the starting element s
  int current = s;
  int pre = s; // Introduce a variable pre to hold the previous element

  int i = 0;
  while (true) {
    if (pre % 2 == 0) {
      current = pre / 2;
    } else {
      current = 3 * pre + 1;
    }
    i++;
    if (se.find(current) != se.end()) {
      cout << i + 1 << endl;
      return 0;
    }
    se.insert(current);
    pre = current; // Update pre to be the current element
  }
}
