#include <bits/stdc++.h>

using namespace std;

int main() {
  int s; cin>>s;
  unordered_set<int> se = {s}; // Use unordered set for faster lookup
  int current = s;
  int i = 1;

  while (true) {
    if (current % 2 == 0) {
      current /= 2;
    } else {
      current = 3 * current + 1;
    }
    i++;
    if (!se.insert(current).second) { // Check and insert in one step
      cout << i << endl;
      return 0;
    }
  }
}
