#include <bits/stdc++.h>

using namespace std;

int main() {
  int s; cin >> s;
  unordered_set<int> se = {s}; // Use unordered set for faster lookup
  int current = s;
  int i = 1;

  // Single loop to generate sequence and check for repeats
  while (se.insert(current).second) { // Attempt to insert and check if newly inserted
    i++;
    if (current % 2 == 0) {
      current /= 2;
    } else {
      current = 3 * current + 1;
    }
  }

  cout << i << endl;
  return 0;
}
