#include <iostream>
#include <unordered_set>

using namespace std;

typedef long long ll;

int main() {
    int s;
    cin >> s;

    unordered_set<int> seen;
    seen.insert(s);

    int ans = 0; // Initialize answer counter

    while (true) {
        if (s % 2 == 0) {
            s /= 2;
        } else {
            s = 3 * s + 1;
        }
        ans++;

        if (s == 1 || !seen.insert(s).second) {
            break;
        }
    }

    cout << ans << endl;
}
