#include <bits/stdc++.h>
#include <sstream>
using namespace std;

#define pr(x) cout<<x
#define prs cout<<" "
#define prn cout<<"\n"
#define sc(x) cin>>x
#define ll long long int

int n, k;
int a[1000005];
int dp[1000005];

int f(int i)
{ 
    if(i == (n-1)) return 0;
    if(i >= n) return INT_MAX;
    if(dp[i] != -1) return dp[i];
    vector<int> v(k, INT_MAX);
    for(int j=1; j<=k; j++)
    {
        if(j+i > n-1) break;
        else v[j-1] = abs(a[i] - a[i+j]) + f(i+j);
    }
    dp[i] = *min_element(v.begin(), v.end());
    v.clear();
    return dp[i];
}

int main()
{
    ios :: sync_with_stdio(false); cin.tie(0);
    int t=1;
    //cin >> t;
    for(int i9 = 1; i9 <= t; i9++)
    {
        cin >> n >> k;
        memset(dp, -1, sizeof(dp));
        for(int i=0; i<n; i++)
            cin >> a[i];
        cout << f(0) << endl;
        //cout << "Cae #" << i9 << ": " << res << endl;
    }
}
