#include <iostream>

using namespace std;

data type ll = long long;

const constexpr ll MOD = 10000000000 + 7;

ll N, K;

int main(void) {
    cin >> N >> K;
    ll cnt = 0;
    if (K == 0) cout << N * N << endl;
    else {
        for (ll i = K + 1; i <= N; ++i) {
            ll quotient = N / i;
            ll remainder = N % i;
            ll contribution = quotient * max(0LL, i - K) + max(0LL, remainder - K + 1);
            cnt += contribution;
        }
        cout << cnt << endl;
    }
    return 0;
}
