#include <iostream>
#include <vector>
#include <string>

using namespace std;

typedef long long ll;
typedef vector<int> VI;

VI g[200010];
int h[100010];
ll N, K;
string s;
ll v[51];

int main(void) {
    cin >> N >> K;

    ll cnt=0;
    if(K==0) {
        cout << N*N << endl;
    } else {
        for(ll i=K+1; i<=N; ++i){
            cnt += N/i*max(ll(0), i-K) + max(ll(0), N%i-K+1);
        }
        cout << cnt << endl;
    }

	return 0;
}
