#include <iostream>
#include <vector>
#include <string>

using namespace std;

typedef long long ll;
typedef vector<int> VI;

VI g[200010];
int h[100010];
ll N, K;
string s;
ll v[51];

int main(void) {
    cin >> N >> K;

    ll ans = 0;
    if (K == 0) {
        cout << N * N << endl;
    } else {
        for (ll i = K + 1; i <= N; ++i) {
            ll quotient = N / i;
            ll remainder = N % i;
            ans += quotient * (i - K) + max(0LL, remainder - K + 1LL);
        }
        cout << ans << endl;
    }

    return 0;
}
