#include <bits/stdc++.h>

#define all(v) v.begin(),v.end()

#define endl '\n'

#define ll long long

using namespace std;

double PI = acos(-1);

void fast()
{
    std::ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);
}

ll arr[10000000], n, k;

vector<ll> a(1e5 + 2, -1), dp(1e5 + 2, -1);

ll minCost(ll indx)
{
    ll path1 = LLONG_MAX;

    if (indx == n - 1)
        return 0;

    if (dp[indx] != -1)
        return dp[indx];

    for (int i = 1; i <= k; i++) // Changed from i = 0 to i = 1 and '< k' to '<= k'
    {
        if ((indx + i) < n) // Updated to use (indx + i) instead of (indx + 1 + i)
            path1 = min(path1, (abs(arr[indx] - arr[indx + i]) + minCost(indx + i)));
    }

    return dp[indx] = path1;
}

int main()
{
    fast();

    cin >> n >> k;

    for (int i = 0; i < n; i++)
        cin >> arr[i];

    cout << minCost(0) << endl;

    return 0;
}
