#include <bits/stdc++.h>

#define all(v) v.begin(),v.end()
#define endl '\n'
#define ll long long

double PI = acos(-1);

using namespace std;

void fast() {
    ios::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
}

ll arr[10000000], n, k;

vector<ll> dp(100002, -1);

ll ans(ll indx) {
    if (indx == n - 1) return 0;

    if (dp[indx] != -1) return dp[indx];

    ll path1 = LLONG_MAX;
    for (int i = 1; i <= k; i++) {
        if ((indx + i) < n)
            path1 = min(path1, abs(arr[indx] - arr[indx + i]) + ans(indx + i));
    }

    return dp[indx] = path1;
}

int main() {
    fast();
    cin >> n >> k;
    for (int i = 0; i < n; i++)
        cin >> arr[i];

    cout << ans(0) << endl;

    return 0;
}
