void update(int x, int delta) {
    while (x <= 5 * n) {
        c[x] += delta;
        x += lowbit(x);
    }
}

int get(int x) {
    int y = 0;
    while (x > 0) {
        y += c[x];
        x -= lowbit(x);
    }
    return y;
}

