#include <algorithm>
#include <iostream>
#include <cstdio>
#include <vector>

#define LOWBIT(x) (x & -(x))

using namespace std;

const int MAXN = 1e6 + 7;

struct Element {
    int value;
    int position;
};

Element a[MAXN];
int n;
long long ans0;
int c[5 * MAXN] = {0};

void update(int x) {
    while (x <= 5 * n) {
        c[x] += 1;
        x += LOWBIT(x);
    }
}

int getsum(int x) {
    int y = 0;
    while (x > 0) {
        y += c[x];
        x -= LOWBIT(x);
    }
    return y;
}

bool check(int threshold) {
    long long total = 0;
    int sum = 0;
    fill(c, c + (5 * n) + 1, 0);
    for (int i = 1; i <= n; i++) {
        sum += (a[i].value >= threshold);
        int adjustedValue = 2 * sum - i + n + 1;
        total += getsum(adjustedValue);
        update(adjustedValue);
    }
    return total >= ans0;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i].value);
        a[i].position = i;
    }
    sort(a + 1, a + n + 1, [](const Element &x, const Element &y) { return x.value < y.value; });
    int l = 1, r = n, ans = 0;
    ans0 = (1LL * n * (n - 1) / 2 + n + 1) / 2LL;
    while (l <= r) {
        int mid = (l + r) / 2;
        if (check(a[mid].value)) ans = mid, l = mid + 1;
        else r = mid - 1;
    }
    printf("%d\n", a[ans].value);
    return 0;
}
