#include "bits/stdc++.h"

#define ll long long

#define PI 3.1415926535897932384626433832795l

#define MAX_NUM 1000000000

using namespace std;

template <typename T> inline T __lcm(T a,T b)
{
    return (a*b)/__gcd(a,b);
}

ll h[1000000];
ll dp[1000000];

int main()
{
    ll n, k;
    cin >> n >> k;
    for(int i = 0; i < n; i++) {
        cin >> h[i];
    }

    memset(dp, -1, sizeof(dp[0]) * 1000000);
    dp[n - 1] = 0; // Base case initialization

    for(int i = n - 2; i >= 0; i--) {
        ll minimum = MAX_NUM;
        for(int j = 1; j <= k && i + j < n; j++) {
            ll temp = fabs(h[i] - h[i + j]) + dp[i + j];
            minimum = min(minimum, temp);
        }
        dp[i] = minimum;
    }

    printf("%lld\n", dp[0]);
}
